const intervalId = setInterval(() => {
  console.log('James');
}, 1000);

setTimeout(() => {
  const promise = new Promise((resolve) => {
    console.log('Richard');
    resolve('Robert');
  });

  promise
      .then((value) => {
        console.log(value);

        setTimeout(() => {
          console.log('Michael');

          clearInterval(intervalId);
        }, 1000);
      });

  console.log('John');
}, 1000);
